notes = ""
print(f"Part   I: {notes.count("B")+notes.count("C")*3}")

notes = ""
total = 0
for duo in zip(notes[::2], notes[1::2]):
  add1 = duo.count("x") == 0
  total += (duo.count("A") * (0 + add1) +
            duo.count("B") * (1 + add1) +
            duo.count("C") * (3 + add1) +
            duo.count("D") * (5 + add1))
print(f"Part  II: {total}")

notes = ""
total = 0
for trio in zip(notes[::3], notes[1::3], notes[2::3]):
  add1 = trio.count("x") == 1
  add2 = (trio.count("x") == 0) * 2
  total += (trio.count("A") * (0 + add1 + add2) +
            trio.count("B") * (1 + add1 + add2) +
            trio.count("C") * (3 + add1 + add2) +
            trio.count("D") * (5 + add1 + add2))
print(f"Part III: {total}")